insertText:
title: "${1:Epic Adventure}"
author: "${2:Your Name}"
version: "${3:1.0.0}"

vars {
    player_name = "${4:Hero}"
    health = 100
    gold = 0
    score = 0
}

stats {
    strength = 5
    agility = 3
    intelligence = 2
    charisma = 4
}

inventory {
    sword = 1
    potion = 2
    key = 0
}

scene start {
    text: "Welcome to your epic adventure, {player_name}!"
    choice "Begin your journey" -> adventure
    choice "Check your stats" -> stats_check
}

scene adventure {
    text: "You find yourself at a crossroads. Your health is {health} and you have {gold} gold."
    if health > 50 {
        text: "You're feeling strong and ready for anything!"
        choice "Take the dangerous path" -> dangerous_path
        choice "Take the safe path" -> safe_path
    } else {
        text: "You're feeling tired and need to rest."
        choice "Find a safe place to rest" -> rest_scene
    }
}

scene stats_check {
    text: "Your current stats:\nStrength: {strength}\nAgility: {agility}\nIntelligence: {intelligence}\nCharisma: {charisma}"
    choice "Return to adventure" -> adventure
}

scene dangerous_path {
    text: "You chose the dangerous path!"
    set health = health - 20
    set gold = gold + 50
    text: "You lost some health but found treasure! Health: {health}, Gold: {gold}"
    choice "Continue exploring" -> adventure
}

scene safe_path {
    text: "You chose the safe path."
    set health = health + 10
    text: "You feel refreshed! Health: {health}"
    choice "Continue exploring" -> adventure
}

scene rest_scene {
    text: "You find a cozy inn and rest for the night."
    set health = 100
    text: "You're fully rested! Health: {health}"
    choice "Continue your adventure" -> adventure
}

scene ${1:scene_id} {
    text: "${2:Scene narrative text}"
    set ${3:variable} = ${4:value}
    choice "${5:Choice text}" -> ${6:target_scene}
}

if ${1:condition} {
    text: "${2:If text}"
    choice "${3:If choice}" -> ${4:if_target}
} else {
    text: "${5:Else text}"
    choice "${6:Else choice}" -> ${7:else_target}
}

macro ${1:heal}(target, amount) {
    set {target}_health = {target}_health + {amount}
    text: "Healed {target} for {amount} points!"
}

inventory {
    sword = 1
    potion = 2
    key = 0
}

stats {
    strength = 5
    agility = 3
    intelligence = 2
}

macro ${1:heal}(target, amount) {
    set {target}_health = {target}_health + {amount}
    if {target}_health > 100 {
        set {target}_health = 100
    }
    text: "Healed {target} for {amount} points! Current health: {target}_health"
}

if {health} > 50 and {strength} > 3 {
    text: "You're strong and healthy enough to face this challenge!"
    choice "Fight the enemy" -> combat_scene
} else if {health} > 30 {
    text: "You're not at full strength, but you can still try."
    choice "Attempt the challenge" -> risky_combat
    choice "Find another way" -> alternative_path
} else {
    text: "You're too weak for this challenge."
    choice "Rest and recover" -> rest_scene
}

