macro search_area(area_name) {
    text: "You search the {area_name} carefully..."
    [ROLL 1d6]
    if [ROLL_RESULT] >= 4 {
        text: "You found something useful!"
        set gold = gold + 10
        choice "Take the treasure" -> continue
    } else {
        text: "You found nothing of interest."
        choice "Continue exploring" -> continue
    }
}
